		CREATE TABLE localizedApplicationProperty (
		  locale                VARCHAR2(100)   NOT NULL,
		  propertyName          VARCHAR2(100)   NOT NULL,
		  propertyValue         VARCHAR2(1024)  NOT NULL,
		  CONSTRAINT localizedAppProp_pk PRIMARY KEY (locale, propertyName)
		);
		
		CREATE TABLE applicationProperty (
		  propertyName          VARCHAR2(100)   NOT NULL,
		  propertyValue         VARCHAR2(1024)  NOT NULL,
		  CONSTRAINT appPorp_pk PRIMARY KEY (propertyName)
		);
