sql-queryset.sql-query:
 
        
		CREATE TABLE LOCALIZED_APPLICATION_PROPERTY (
		  LOCALE_CODE            VARCHAR2(100)   NOT NULL,
		  PROPERTY_NAME          VARCHAR2(100)   NOT NULL,
		  PROPERTY_VALUE         VARCHAR2(1024)  NOT NULL,
		  CONSTRAINT LOCALIZED_APP_PROP_PK PRIMARY KEY (LOCALE_CODE, PROPERTY_NAME)
		);
		
		CREATE TABLE APPLICATION_PROPERTY (
		  PROPERTY_NAME          VARCHAR2(100)   NOT NULL,
		  PROPERTY_VALUE         VARCHAR2(1024)  NOT NULL,
		  CONSTRAINT APP_PROP_PK PRIMARY KEY (PROPERTY_NAME)
		);      
						
                 
        CREATE TABLE PLUGIN_DATA (
            PLUGIN_ID             INTEGER NOT NULL,
            NAME                  VARCHAR2(200) NOT NULL,
            CREATION_DATE         DATE DEFAULT SYSDATE NOT NULL,
            MODIFIED_DATE         DATE DEFAULT SYSDATE NOT NULL,
            DATA                  BLOB,
            CONSTRAINT PLUGIN_DATA_PK PRIMARY KEY (PLUGIN_ID)
        );     
        
        CREATE UNIQUE INDEX PLUGIN_DATA_NAME_IDX ON PLUGIN_DATA (NAME);
		
		CREATE TABLE SEQUENCER (
		    SEQUENCER_ID           INTEGER NOT NULL,
		    NAME                   VARCHAR2(200) NOT NULL,
		    VALUE                  INTEGER NOT NULL,
		    CONSTRAINT SEQUENCER_PK PRIMARY KEY (SEQUENCER_ID)
		); 		
		     
		CREATE UNIQUE INDEX SEQUENCER_NAME_IDX ON SEQUENCER (NAME);
				
				
		CREATE TABLE I18N_LOCALE (
			 LOCALE_ID      INTEGER NOT NULL,
			 LANGUAGE      VARCHAR2(100),
			 COUNTRY       VARCHAR2(100),
			 VARIANT       VARCHAR2(100),
			 ENCODING      VARCHAR2(100),
		     CREATION_DATE  DATE DEFAULT SYSDATE NOT NULL,
		     MODIFIED_DATE  DATE DEFAULT SYSDATE NOT NULL,
		     CONSTRAINT I18N_LOCALE_PK PRIMARY KEY (LOCALE_ID)
    	);
		
		
		INSERT INTO I18N_LOCALE ( LOCALE_ID ,LANGUAGE, COUNTRY, VARIANT, ENCODING ) VALUES (1, 'ko', 'KR', NULL, 'UTF-8');
				
		CREATE TABLE I18N_TEXT (
		     TEXT_ID                INTEGER NOT NULL,
		     LOCALE_CODE            VARCHAR2(100)   NOT NULL,
		     TEXT_KEY               VARCHAR2(200)   NOT NULL,		    
		     TEXT                   VARCHAR2(2000)  NOT NULL,	
			 CREATION_DATE          DATE DEFAULT SYSDATE NOT NULL ,
		     MODIFIED_DATE          DATE DEFAULT SYSDATE NOT NULL ,
		     CONSTRAINT I18N_TEXT_PK PRIMARY KEY (TEXT_ID)
		);
		
		
		CREATE TABLE KOREA_ZIPCODE (
		    ZIPCODE_ID             INTEGER NOT NULL,
		    ZIPCODE                CHAR(7),
		    SIDO                   VARCHAR2(50), 
		    GUGUN                  VARCHAR2(100),  
		    DONG                   VARCHAR2(200),
		    BUNJI		               VARCHAR2(100),
		    CONSTRAINT ZIPCODE_PK PRIMARY KEY (ZIPCODE_ID)		    
		);
				
