		CREATE TABLE REGION (
		  REGION_ID              INTEGER NOT NULL,
			COUNTRY_ID             INTEGER,
			REGION_CODE            VARCHAR2(300) NULL,
			NAME                   VARCHAR2(300) NULL,
			ENABLED                NUMBER(1, 0) DEFAULT 1,
			CONSTRAINT REGION_PK PRIMARY KEY (REGION_ID)
		);

